import { Upload, Modal, Select, Radio, Checkbox, InputNumber, Collapse, Input, Tooltip } from 'antd';
import { DndProvider, useDrag, useDrop, createDndContext } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import update from 'immutability-helper';
import { UploadOutlined } from '@ant-design/icons';
import React, { useRef } from 'react'
import {
    withScriptjs,
    withGoogleMap,
    GoogleMap,
    Marker
} from "react-google-maps";
const { Option } = Select;
const { Panel } = Collapse;
const { TextArea } = Input;

import { PlusOutlined } from '@ant-design/icons';
import { createRef } from 'react';


function getBase64(file) {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });
}

class Utilities {

    constructor(th) {
        this.th = th;
    }

    inputGenerator = (title, placeholder, callback, state) => {
        return (
            <div className={'subitem simpleInput'}>
                <label>{title}</label>
                <Input placeholder={placeholder} name={state} onChange={callback} />
            </div>
        )
    }

    numberGenerator = (title, placeholder, callback, name, visibility) => {
        if (!visibility) return null;
        return (
            <div className={'subitem numberInput'}>
                <label className={'title'}>{title}</label>
                <InputNumber
                    style={{ width: "50%" }}
                    name={name}
                    className={'input'}
                    placeholder={placeholder}
                    formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                    parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    onBlur={callback}
                />
            </div>
        )
    }

    textAreaGeneretor = (title, placeholder, callback, name) => {
        return (
            <div className={'subitem textAreaInput'}>
                <label>{title}</label>
                <TextArea
                    name={name}
                    placeholder={placeholder}
                    autoSize={{ minRows: 2, maxRows: 12 }}
                    onChange={callback}
                    onBlur={callback}
                />
            </div>
        )
    }

    priceİnputGenerator = (title, minName, maxName, min, step, Callback, visibility) => {
        if (!visibility) return null
        return (
            <div className={'subitem priceInput'}>
                <div className={'item'}><label>{title}</label></div>
                <div className={'item'}>
                    <InputNumber
                        placeholder={"Min."}
                        onBlur={Callback}
                        onInput={Callback}
                        min={min} step={step} name={minName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                -
                <InputNumber
                        placeholder={"Max."}
                        onBlur={Callback}
                        onInput={Callback}
                        min={min} step={step} name={maxName}
                        formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                        parser={value => value.replace(/\$\s?|(,*)/g, '')}
                    />
                </div>
            </div>
        )
    }

    selectGenerator = (title, options, name, selected, callback, sort, selectAll) => {
        if (!options || options.length < 1) return null
        if (sort) {
            options.sort((a, b) => {
                let fa = a.name.toLowerCase()
                let fb = b.name.toLowerCase()

                if (fa < fb) return -1
                if (fa > fb) return 1
                return 0
            })
        }
        return (<div className={'subitem selectInput'}>
            <div className={'item'}><label>{title}</label></div>
            <div className={'item'}>
                <Select virtual={false} onSelect={callback} placeholder={"Seçin"} value={selected} name={name} style={{ width: "50%" }}>
                    {selectAll ? <Option state={name} value={0}>Hamısı</Option> : null}
                    {options.map((w, i) => <Option state={name} key={new Date().getTime() + i} value={w.id}>{w.name}</Option>)}
                </Select>
            </div>
        </div>)
    }

    checkBoxGenerator = (title, callback, name, visible) => {
        if (!visible) return null
        return (
            <div className={'subitem checkInput'}>
                <Checkbox state={name} onChange={callback}>{title}</Checkbox>
            </div>
        )
    }


    radioGenerator = (label, values, defaultValue, callback) => {
        if (!values) return null
        return (
            <div className={"subitem radioInput"}>
                <label>{label}</label> <br />
                <Radio.Group onChange={callback} defaultValue={defaultValue} buttonStyle="solid">
                    {values.map((w, i) => <Radio.Button key={new Date().getTime() + i} value={w.id}>{w.name}</Radio.Button>)}

                </Radio.Group>
            </div>
        )
    }

    /* 
    
    IMAGE
    IMAGE
    IMAGE
    
    */

    RNDContext = createDndContext(HTML5Backend);

    type = 'DragableUploadList';

    DragableUploadListItem = ({ originNode, moveRow, file, fileList }) => {
        const ref = React.useRef();
        const index = fileList.indexOf(file);
        const [{ isOver, dropClassName }, drop] = useDrop({
            accept: this.type,
            collect: monitor => {
                const { index: dragIndex } = monitor.getItem() || {};
                if (dragIndex === index) {
                    return {};
                }
                return {
                    isOver: monitor.isOver(),
                    dropClassName: dragIndex < index ? ' drop-over-downward' : ' drop-over-upward',
                };
            },
            drop: item => {
                moveRow(item.index, index);
            },
        });
        const [, drag] = useDrag({
            item: { type, index },
            collect: monitor => ({
                isDragging: monitor.isDragging(),
            }),
        });
        drop(drag(ref));
        const errorNode = (
            <Tooltip title="Upload Error" getPopupContainer={() => document.body}>
                {originNode.props.children}
            </Tooltip>
        );
        return (
            <div
                ref={ref}
                className={`ant-upload-draggable-list-item ${isOver ? dropClassName : ''}`}
                style={{ cursor: 'move' }}
            >
                {file.status === 'error' ? errorNode : originNode}
            </div>
        );
    };


    moveRow =
        (dragIndex, hoverIndex) => {
            const dragRow = fileList[dragIndex];
            setFileList(
                update(fileList, {
                    $splice: [
                        [dragIndex, 1],
                        [hoverIndex, 0, dragRow],
                    ],
                }),
            );
        }


    manager = useRef(RNDContext);

    imageUploadGenerator = (fileList, previewVisible, previewTitle, previewImage) => {
        return (
            <div className={"subitem"}>
                <DndProvider manager={this.RNDContext.current.dragDropManager}>
                    <Upload
                        action="http://192.168.1.107:5566/api/bina/image"
                        name={"image"}
                        multiple={true}
                        listType="picture-card"
                        fileList={fileList}
                        onPreview={this.handlePreview}
                        onChange={this.handleChange}
                        itemRender={(originNode, file, currFileList) => (
                            <this.DragableUploadListItem
                                originNode={originNode}
                                file={file}
                                fileList={currFileList}
                                moveRow={this.moveRow}
                            />
                        )}
                    >
                        {fileList.length >= 25 ? null : <div>
                            <PlusOutlined />
                            <div style={{ marginTop: 8 }}>Upload</div>
                        </div>}
                    </Upload>
                </DndProvider>
                <Modal
                    visible={previewVisible}
                    title={previewTitle}
                    footer={null}
                    onCancel={this.handleCancel}
                >
                    <img alt="example" style={{ width: '100%' }} src={previewImage} />
                </Modal>
            </div>
        )
    }

    getImageState = {
        previewVisible: false,
        previewImage: '',
        previewTitle: '',
        fileList: []
    }

    handleCancel = () => this.th.setState({ previewVisible: false });

    handlePreview = async (file) => {
        if (!file.url && !file.preview) {
            file.preview = await getBase64(file.originFileObj);
        }

        this.th.setState({
            previewImage: file.url || file.preview,
            previewVisible: true,
            previewTitle: file.name || file.url.substring(file.url.lastIndexOf('/') + 1),
        });
    };

    handleChange = async ({ fileList }) => {
        this.th.setState({ fileList: [...fileList] })
        this.th.setState({
            selected:
            {
                ...this.th.state.selected,
                images: [...fileList].filter(w => w.response).map(w => w.response.fileName)

            }
        })
    };

    /*
    
    IMAGE
    IMAGE
    IMAGE

    */
    googleMap = (lat, lan, callback, title) => {
        return (
            <div className={"subitem"} style={{ height: '100%', width: '100%', padding: "20px" }}>
                <label>{title}</label>
                <RegularMap
                    googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyD6Vz_IKnktPC_TLl9DAmm_BpxokzQ4fIQ"
                    loadingElement={<div style={loadingElementStyle} />}
                    containerElement={<div style={containerElementStyle} />}
                    mapElement={<div style={mapElementStyle} />}
                    info={{ lat: lat, lan: lan, callback: callback }}
                />
            </div>
        )
    }
}



